import React, { Component } from "react";

class Tasks extends Component {
  constructor(props) {
    this.props = props;
  }
  state = {
    checked: false,
  };
  handleDelete = (event) => {
    event.target.parentNode.style = "display: none";
  };

  handleCheck = (event) => {
    if (this.state.checked == false) {
      event.target.nextSibling.style = "text-decoration: line-through";
      this.setState({
        checked: true,
      });
    } else {
      event.target.nextSibling.style = "text-decoration: line-through";
      this.setState({
        checked: false,
      });
    }
  };

  render() {
    return (
      <div className={`list-item ${this.state.checked ? "checked" : ""}`}>
        <input
          type="checkbox"
          className="checkbox"
          onClick={this.handleCheck}
        ></input>
        <div className="text">{this.props.data}</div>

        <div className="del" onClick={this.handleDelete}>
          -
        </div>
      </div>
    );
  }
}

export default Tasks;
